    @IBAction func rightbuttontap(_ sender: Any) {
        if imgnumber >= 4 {
            imgnumber = 1
        }
        else {
            imgnumber += 1
        }
         mainview.image = UIImage(named: "IMG_\(imgnumber)")
    }
}

